contracts/BridgeOut.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/draft-IERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";

import "./interfaces/IGalaGameItems.sol";
import "./interfaces/IGalaERC20.sol";
import "./interfaces/IERC721GalaMintableBurnable.sol";

import "./TokenHolder.sol";
import "./Getters.sol";
import "./Setters.sol";
import "./Payload.sol";

/// @title Bridge Out
/// @notice This contract allows to bridge tokens out from EVM blockchain to the Play blockchain.
/// @author Piotr "pibu" Buda
abstract contract BridgeOut is Getters, Setters, TokenHolder {
    using SafeERC20 for IERC20;

    event BridgeTokens(bytes32 emitter, uint16 chainId, uint64 sequence, bytes32 nonce, bytes payload);

    /// @notice Interact with this method to start the bridging out process. The bridge has to be approved to transfer tokens to be bridged.
    /// @param token the address of the token contract, must be non-zero
    /// @param amount the amount of tokens to bridge out, must be 1 for ERC-721
    /// @param tokenId the id of the token to bridge out, disregarded for ERC-20 tokens
    /// @param destinationChainId chain id of the destination (for routing purposes)
    /// @param recipient the recipient on the Play blockchain, formatted in a way understandable by it
    function bridgeOut(address token, uint256 amount, uint256 tokenId, uint16 destinationChainId, bytes calldata recipient) external whenNotPaused {
        _bridgeOutInternal(msg.sender, token, amount, tokenId, destinationChainId, recipient);
    }

    function _bridgeOutInternal(
        address tokenOwner,
        address token,
        uint256 amount,
        uint256 tokenId,
        uint16 destinationChainId,
        bytes calldata recipient
    ) internal {
        require(token != address(0), "INVALID_TOKEN");
        require(recipient.length > 0, "INVALID_RECIPIENT");
        require(isAllowedDestinationChainId(token, destinationChainId), "DESTINATION_NOT_ALLOWED");

        TokenType tokenType = getTokenType(token);

        uint256 baseType;
        uint256 instance;
        uint256 transferredAmount;

        if (tokenType == TokenType.ERC20) {
            //in case of ERC20 the tokenId parameter is disregarded
            (baseType, instance) = (DEFAULT_TOKEN_BASE_TYPE, 0);
            transferredAmount = transferInERC20(tokenOwner, token, amount);
        } else if (tokenType == TokenType.ERC721) {
            //in case of ERC721 the amount parameter is disregarded
            require(amount == 1, "INVALID_AMOUNT");
            (baseType, instance) = (DEFAULT_TOKEN_BASE_TYPE, tokenId);
            transferInERC721(tokenOwner, token, tokenId);
            transferredAmount = 1;
        } else if (tokenType == TokenType.ERC1155) {
            (baseType, instance) = getERC1155BaseTypeAndInstance(token, tokenId);
            transferInERC1155(tokenOwner, token, tokenId, amount, baseType);
            transferredAmount = instance == 0 ? amount : 1;
        } else {
            revert("UNKNOWN_TOKEN_TYPE");
        }

        publishBridgeOutMessage(token, baseType, destinationChainId, transferredAmount, instance, recipient);
    }

    /// @notice Allows to start the bridge out process using EIP-2612 permit to avoid ERC20.approve() call
    /// @param token the address of the token contract, must be non-zero
    /// @param amount the amount of tokens to bridge out
    /// @param destinationChainId chain id of the destination (for routing purposes)
    /// @param recipient the recipient on the Play blockchain, formatted in a way understandable by it
    /// @param deadline the deadline for the permit
    /// @param v the v part of the signature
    /// @param r the r part of the signature
    /// @param s the s part of the signature
    function bridgeOutWithPermit(
        address token,
        uint256 amount,
        uint16 destinationChainId,
        bytes calldata recipient,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external whenNotPaused {
        require(getTokenType(token) == TokenType.ERC20, "ERC20_ONLY");

        try IERC20Permit(token).permit(msg.sender, address(this), amount, deadline, v, r, s) {
            _bridgeOutInternal(msg.sender, token, amount, 0, destinationChainId, recipient);
        } catch Error(string memory reason) {
            revert(reason);
        } catch {
            // supported permit impl would have to revert with a reason
            // so it means permit doesn't exist on the token contract
            revert("PERMIT_NOT_SUPPORTED");
        }
    }

    /// @notice Transfers ERC20 tokens to the bridge and burns them if the bridge for the token is in burning mode.
    /// @param token address of the ERC20 token
    /// @param amount the amount to be bridged out
    function transferInERC20(address tokenOwner, address token, uint256 amount) private returns (uint256 transferredAmount) {
        require(amount != 0, "INVALID_AMOUNT");
        require(isEnabled(token, DEFAULT_TOKEN_BASE_TYPE), "DISABLED");

        uint256 balance = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(tokenOwner, address(this), amount);
        transferredAmount = IERC20(token).balanceOf(address(this)) - balance;
        require(transferredAmount != 0, "TRANSFER_FAILED");

        if (isBurningBridge(token, DEFAULT_TOKEN_BASE_TYPE)) {
            //this bridge assumes that all burnable ERC-20 assets will implement this burn method
            IGalaERC20(token).burn(transferredAmount);
        }
    }

    /// @notice Transfers an ERC721 token and burns it if the bridge for the token is in burning mode.
    /// @param token address of the ERC721 token
    /// @param tokenId the id of the token to bridge
    function transferInERC721(address tokenOwner, address token, uint256 tokenId) private returns (uint256 transferredAmount) {
        require(isEnabled(token, DEFAULT_TOKEN_BASE_TYPE), "DISABLED");

        transferredAmount = 1;

        IERC721GalaMintableBurnable(token).safeTransferFrom(tokenOwner, address(this), tokenId);

        if (isBurningBridge(token, DEFAULT_TOKEN_BASE_TYPE)) {
            //this bridge assumes that all burnable ERC-721 assets will implement this burn method
            IERC721GalaMintableBurnable(token).burn(tokenId);
        }
    }

    /// @notice Transfers ERC1155 tokens to the bridge and burns them if the bridge for the token is in burning mode.
    /// @param token address of the ERC1155 token
    /// @param tokenId the id of the token to bridge
    /// @param amount the amount of the said token to be bridged
    function transferInERC1155(address tokenOwner, address token, uint256 tokenId, uint256 amount, uint256 baseType) private {
        require(amount != 0, "INVALID_AMOUNT");
        require(isEnabled(token, baseType), "DISABLED");

        IERC1155(token).safeTransferFrom(tokenOwner, address(this), tokenId, amount, "");

        if (isBurningBridge(token, baseType)) {
            uint256[] memory _ids = new uint256[](1);
            uint256[] memory _values = new uint256[](1);
            _ids[0] = tokenId;
            _values[0] = amount;
            //this bridge assumes that all burnable ERC-1155 assets will implement this burn method
            IGalaGameItems(token).burn(address(this), _ids, _values);
        }
    }

    /// @dev This method publishes a BridgeTokens event in a specific format that off-chain parts of the bridge (i.e. the Verifier nodes)
    /// can perform other necessary steps in the bridging process.
    function publishBridgeOutMessage(
        address token,
        uint256 baseType,
        uint16 destinationChainId,
        uint256 amount,
        uint256 instance,
        bytes calldata recipient
    ) internal {
        bytes memory tokenClassKey = getTokenClassKey(token, baseType, true);
        bytes memory payload = Payload.encode(destinationChainId, amount, instance, recipient, tokenClassKey);

        bytes32 emitter = bytes32(uint256(uint160(address(this))));
        uint64 msgSequence = useSequence();

        emit BridgeTokens(emitter, chainId(), msgSequence, keccak256(abi.encodePacked(emitter, chainId(), msgSequence)), payload);
    }

    /// @dev Queries the Gala ERC1155 contracts to determine the base type and instance of a given token.
    function getERC1155BaseTypeAndInstance(address token, uint256 tokenId) internal pure returns (uint256 baseType, uint256 instance) {
        IGalaGameItems ggi = IGalaGameItems(token);
        if (ggi.isFungible(tokenId)) {
            baseType = tokenId;
            instance = 0;
        } else {
            baseType = ggi.getNonFungibleBaseType(tokenId);
            instance = ggi.getNonFungibleIndex(tokenId);
        }
    }
}

