contracts/BatchBridgeOut.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./BridgeOut.sol";

contract BatchBridgeOut is BridgeOut {
    uint8 private constant BRIDGE_MODE_UNKNOWN = 0;
    uint8 private constant BRIDGE_MODE_BURNING = 1;
    uint8 private constant BRIDGE_MODE_LOCKING = 2;

    function batchBridgeOut(
        address token,
        uint256[] calldata amounts,
        uint256[] calldata tokenIds,
        uint16 destinationChainId,
        bytes calldata recipient
    ) external whenNotPaused {
        require(token != address(0), "INVALID_TOKEN");
        require(getTokenType(token) == TokenType.ERC1155, "ERC1155_ONLY");
        require(tokenIds.length != 0, "NO_TOKENS");
        require(tokenIds.length < type(uint8).max, "TOO_MANY_TOKENS");
        require(tokenIds.length == amounts.length, "INVALID_LENGTHS");
        require(isAllowedDestinationChainId(token, destinationChainId), "DESTINATION_NOT_ALLOWED");
        require(recipient.length > 0, "INVALID_RECIPIENT");

        bytes memory payload = Payload.encodeHeader(destinationChainId, recipient, uint8(tokenIds.length));

        // Bridge setting is initialized to a value that is neither BRIDGE_MODE_BURNING nor BRIDGE_MODE_LOCKING
        // but will be set to BRIDGE_MODE_BURNING or BRIDGE_MODE_LOCKING in the loop below. 
        // It's done this way to optimize the check that all token bridges are either burning or locking.
        uint8 bridgeSetting = BRIDGE_MODE_UNKNOWN;

        for (uint256 i = 0; i < tokenIds.length; i++) {
            (uint256 baseType, uint256 instance) = getERC1155BaseTypeAndInstance(token, tokenIds[i]);

            // calling this here ensures that the token bridge exists and is enabled
            // otherwise we shouldn't bother to check the bridge mode
            bytes memory tokenClassKey = getTokenClassKey(token, baseType, true);
            uint8 bridgeMode = isBurningBridge(token, baseType) ? BRIDGE_MODE_BURNING : BRIDGE_MODE_LOCKING;

            if (bridgeSetting == BRIDGE_MODE_UNKNOWN) {
                bridgeSetting = bridgeMode;
            } else {
                require(bridgeSetting == bridgeMode, "MIXED_BURNING_AND_LOCKING_NOT_SUPPORTED");
            }

            // encoding the payload here instead of using the Payload library saves a significant amount of gas
            payload = bytes.concat(payload, bytes32(amounts[i]), bytes32(instance), bytes2(uint16(tokenClassKey.length)), tokenClassKey);
        }

        batchTransferInERC1155(token, amounts, tokenIds, bridgeSetting == BRIDGE_MODE_BURNING);

        bytes32 emitter = bytes32(uint256(uint160(address(this))));
        uint64 msgSequence = useSequence();

        emit BridgeTokens(emitter, chainId(), msgSequence, keccak256(abi.encodePacked(emitter, chainId(), msgSequence)), payload);
    }

    function batchTransferInERC1155(address token, uint256[] calldata amounts, uint256[] calldata tokenIds, bool isBurning) internal {
        IERC1155(token).safeBatchTransferFrom(msg.sender, address(this), tokenIds, amounts, "");

        if (isBurning) {
            IGalaGameItems(token).burn(address(this), tokenIds, amounts);
        }
    }
}

