import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

/// @title Token Holder
/// @notice This is a helper contract.
/// @author Piotr "pibu" Buda
abstract contract TokenHolder is ERC1155Holder, ERC721Holder {
    function onERC721Received(address operator, address from, uint256, bytes memory) public virtual override returns (bytes4) {
        require(from == address(this) || operator == address(this), "DIRECT_DEPOSITS_NOT_ALLOWED");
        return this.onERC721Received.selector;
    }

    function onERC1155Received(address operator, address from, uint256, uint256, bytes memory) public virtual override returns (bytes4) {
        require(from == address(this) || operator == address(this), "DIRECT_DEPOSITS_NOT_ALLOWED");
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(address operator, address from, uint256[] memory, uint256[] memory, bytes memory) public virtual override returns (bytes4) {
        require(from == address(this) || operator == address(this), "DIRECT_DEPOSITS_NOT_ALLOWED");
        return this.onERC1155BatchReceived.selector;
    }
}

