//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./Structs.sol";

library Payload {
    function encodeHeader(uint16 destinationChainId, bytes calldata recipient, uint8 numTokens) internal pure returns (bytes memory) {
        return
            bytes.concat(
                bytes1(0x01), //TODO: hardcoded for now as we only support 1 version
                bytes2(destinationChainId),
