        pos += 1;

        bt = new Structs.BridgeToken[](numTokens);

        for (uint256 i = 0; i < numTokens; i++) {
            uint256 amount = uint256(bytes32(payload[pos:pos + 32]));
            pos += 32;
            uint256 instance = uint256(bytes32(payload[pos:pos + 32]));
            pos += 32;
            uint16 tokenClassKeyLength = uint16(bytes2(payload[pos:pos + 2]));
            pos += 2;
            bytes memory tokenClassKey = payload[pos:pos + tokenClassKeyLength];
            pos += tokenClassKeyLength;

            bt[i] = Structs.BridgeToken(tokenClassKey, amount, instance);
        }

        // Ensure that the payload was fully consumed
        require(pos == payload.length, "PAYLOAD_DECODED_LENGTH_MISMATCH");
    }
}

